import React from 'react';
import {useSelector } from 'react-redux';
import {selectSignedIn} from "../features/userLogin";
import "../css/post.css"
import Navbar from "./Navbar";
const Blog = () => {
    const isSignedIn = useSelector(selectSignedIn);
    return (
        <>
        <Navbar/>
        <div className="home_page">
            <h1 className="blog_page_name">
            </h1>
            {isSignedIn && (<div>
            <input type="text" id="post_name" name="post_name"></input>
            <textarea type="text" id="comment" name="post_commentname"/>
            <button className="post">Add New Post</button>
            </div>)}  

        </div>

        </>
    );
}

export default Blog;
